using Newtonsoft.Json;
using Newtonsoft.Json.Linq;
using System;
using System.Collections.Generic;
using System.IO;
using System.Reflection;
using System.Security.Cryptography.X509Certificates;
using System.Text;
using Xunit;

namespace JsonWebToken.Tests
{
    public class JsonWebKeyTests
    {
        [Theory]
        [MemberData(nameof(GetJsonKeys))]
        public void CreateFromJson(string json, string kid, string alg)
        {
            var jwk = Jwk.FromJson(json);

            Assert.Equal(jwk.Kid, kid);
            if (!(jwk.Alg is null))
            {
                Assert.Equal(Encoding.UTF8.GetString(jwk.Alg), alg);
            }
        }

        [Theory]
        [MemberData(nameof(GetCertificates))]
        public void CreateFromCertificate(X509Certificate2 certificate, bool hasPrivateKey, int keySize)
        {
            var jwk = Jwk.FromX509Certificate(certificate, hasPrivateKey);
            Assert.Equal(keySize, jwk.KeySizeInBits);

            if (hasPrivateKey)
            {
                Assert.True(jwk.HasPrivateKey);
            }
        }

        public static IEnumerable<object[]> GetJsonKeys()
        {
            var fixture = new KeyFixture();
            foreach (var key in fixture.Jwks.GetKeys(null))
            {
                yield return new object[] { key.ToString(), key.Kid, Encoding.UTF8.GetString(key.Alg ?? new byte[0]) };
            }
        }

        public static IEnumerable<object[]> GetCertificates()
        {
            return Certificates;
        }

        [Fact]
        public void Thumbprint()
        {
            // https://tools.ietf.org/html/rfc7638#section-3.1
            var key = new RsaJwk
            (
                e: "AQAB",
                n: "0vx7agoebGcQSuuPiLJXZptN9nndrQmbXEps2aiAFbWhM78LhWx4cbbfAAtVT86zwu1RK7aPFFxuhDR1L6tSoc_BJECPebWKRXjBZCiFV4n3oknjhMstn64tZ_2W-5JsGY4Hc5n9yBXArwl93lqt7_RN5w6Cf0h4QyQ5v-65YGjQR0_FDW2QvzqY368QQMicAtaSqzs8KJZgnYb9c7d0zgdAZHzu6qMQvRL5hajrn1n91CbOpbISD08qNLyrdkt-bFTWhAI4vMQFh6WeZu0fM4lFd2NcRwr3XPksINHaQ-G_xBniIqbw0Ls1jF44-csFCur-kEgU8awapJzKnqDKgw"
            )
            {
                Kid = "2011-04-29",
                Alg = ((SignatureAlgorithm)"RS256").Utf8Name
            };

            var thumbprint = key.ComputeThumbprint();

            Assert.Equal(Encoding.UTF8.GetBytes("NzbLsXh8uDCcd-6MNwXF4W_7noWXFZAfHkxZsRGC9Xs"), thumbprint);
        }

        public static IEnumerable<object[]> Certificates
        {
            get
            {
                yield return new object[] { CertSha256_1024, true, 1024 };
                yield return new object[] { CertSha256_1024_Public, false, 1024 };
                yield return new object[] { CertSha256_2048, true, 2048 };
                yield return new object[] { CertSha256_2048_Public, false, 2048 };
                yield return new object[] { CertSha384_2048, true, 2048 };
                yield return new object[] { CertSha384_2048_Public, false, 2048 };
                yield return new object[] { CertSha512_2048, true, 2048 };
                yield return new object[] { CertSha512_2048_Public, false, 2048 };
            }
        }

        public const string CertPassword = "abcd";

        public static string Sha256_1024 = @"";
        public static string Sha256_1024_Public = "";
        public static X509Certificate2 CertSha256_1024 = new X509Certificate2(Convert.FromBase64String(Sha256_1024), "SelfSigned1024_SHA256", X509KeyStorageFlags.PersistKeySet | X509KeyStorageFlags.Exportable);
        public static X509Certificate2 CertSha256_1024_Public = new X509Certificate2(Convert.FromBase64String(Sha256_1024_Public), "SelfSigned1024_SHA256");

        public static string Sha256_2048 = @"";
        public static string Sha256_2048_Public = @"";
        public static X509Certificate2 CertSha256_2048 = new X509Certificate2(Convert.FromBase64String(Sha256_2048), "SelfSigned2048_SHA256", X509KeyStorageFlags.PersistKeySet | X509KeyStorageFlags.Exportable);
        public static X509Certificate2 CertSha256_2048_Public = new X509Certificate2(Convert.FromBase64String(Sha256_2048_Public), "SelfSigned2048_SHA256");

        public static string Sha384_2048 = @"";
        public static string Sha384_2048_Public = "";
        public static X509Certificate2 CertSha384_2048 = new X509Certificate2(Convert.FromBase64String(Sha384_2048), "SelfSigned2048_SHA384", X509KeyStorageFlags.PersistKeySet | X509KeyStorageFlags.Exportable);
        public static X509Certificate2 CertSha384_2048_Public = new X509Certificate2(Convert.FromBase64String(Sha384_2048_Public), "SelfSigned2048_SHA384");

        public static string Sha512_2048 = @"";
        public static string Sha512_2048_Public = "";
        public static X509Certificate2 CertSha512_2048 = new X509Certificate2(Convert.FromBase64String(Sha512_2048), "SelfSigned2048_SHA512", X509KeyStorageFlags.PersistKeySet | X509KeyStorageFlags.Exportable);
        public static X509Certificate2 CertSha512_2048_Public = new X509Certificate2(Convert.FromBase64String(Sha512_2048_Public), "SelfSigned2048_SHA512");
    }
}
